package JavaBasic.exam;

public class Exam2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Gob=1, i;
		
		for(i=1; i<=10; i++){
					
			  Gob = Gob * i;
			  System.out.println("i가 "+i+"일때 : "+Gob);
			 
		}
		
		System.out.println("1부터 10까지의 곱은 "+ Gob +"입니다!");		

	}

}
