public class Exam8_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		
		for(i=1 ; i<=5 ; i++){
			for(j=1; j<=i ; j++){
				System.out.print("$  ");				
			}//inner for
			System.out.println("\n");				
		}//outer for			
	}//main
}//class